mapper.select:
SELECT lr.subtasks_id AS subtasksId, COUNT( lr.subtasks_id ) totalCount, SUM( IF ( lr.phone_status IN ( 4, 8), 1, 0 )) AS successCount, SUM( IF ( lr.phone_status IN (8), 1, 0 )) AS registerSuccessCount, SUM( IF ( lr.phone_status = 6, 1, 0 )) AS errorCount FROM cd_get_phone lr WHERE lr.subtasks_id > 0 AND subtasks_id IN
#{item}
GROUP BY lr.subtasks_id

SELECT SUM( CASE WHEN phone_status = 1 THEN 1 ELSE 0 END ) AS waitCount, SUM( CASE WHEN phone_status = 2 THEN 1 ELSE 0 END ) AS startRegisterCount, SUM( CASE WHEN phone_status IN (3, 4 ) THEN 1 ELSE 0 END ) AS submitRegisterCount, SUM( CASE WHEN phone_status = 8 THEN 1 ELSE 0 END ) AS successRegisterCount, SUM( CASE WHEN phone_status = 6 THEN 1 ELSE 0 END ) AS errorRegisterCount FROM cd_get_phone p LEFT JOIN cd_register_subtasks s ON p.subtasks_id = s.id
AND s.task_id = #{dto.tasksId}
AND p.country = #{dto.countryCode}
and p.create_time between CONCAT(#{dto.createStartTime}, ' 00:00:00') and CONCAT(#{dto.createEndTime}, ' 23:59:59')
order by p.create_time desc
